import pandas as pd
import argparse
import numpy as np
from influxdb import InfluxDBClient

#print(brics)
def read_data():
    with open('worldbank.csv') as f: #Input the name of your csv file here
        return [x.split(',') for x in f.readlines()[1:]]
def read_header():
    with open('worldbank.csv') as f: #Input the name of your csv file here
        return [x.split(',') for x in f.readlines()[:1]]

